import React, {Component} from 'react';
import moment from "moment";

export default class AllNewsIntro extends Component {

    getCheckBoxes(){
       const allTag = this.props.news.map((item)=>{
           return item.tag;
       });
        console.log(allTag);
        let sortedTag = [];

        for (let i = 0; i < allTag.length; i++){
            if(!sortedTag.includes(allTag[i])){
                sortedTag.push(allTag[i]);
            }

            if(i === allTag.length - 1){
                sortedTag.sort();
            }
        }
        console.log(sortedTag);
    }
    render() {

        return (
            <div className="intro" >

                <div className="left">
                    {this.getCheckBoxes()}
                </div>

                <div className="right">
                    <img src={'/src/main/dots.png'} className={'dots'} alt=""/>
                    <h1>Последние новости</h1>
                    <div className="intro-news">
                        {this.sortNews()}
                    </div>
                </div>
            </div>
        )
    }




    sortNews(){
        return this.props.news.slice(0,3).map((item, index)=>{
            return <div className="intro-article-news" key={index}>
                <div className="top">
                    <div className="tag">
                        {this.getTag(item.tag)}
                    </div>
                    <div className="date">
                        {moment(item.created).format('DD.MM.YYYY')}
                    </div>
                </div>
                <div className="title">{item.title}</div>
            </div>
        });
    }




    getTag(tag){
        switch (tag) {
            case 'party':
                return 'Развлечение';
            case 'paertners':
                return 'Партнерство';
            case 'local':
                return 'Локальные';
            case 'trade':
                return 'Биржа';
            default: return ''

        }
    }
}
