import React, {Component} from 'react';
import SVGlogo from "../main/SVGlogo";
import Tilt from "react-tilt";

export default class Contacts extends Component {
    render() {
        return (
            <div className="contacts" style={{backgroundImage: `url(./src/main/mainLayer.svg)`}}>
                <div className="width-layer">
                    <div className="left">
                        <p>You can easily write to us right now and we will gladly read your message and can be answered if we want</p>
                        <div className="contact-form">
                            <div className="box-input">
                                <label htmlFor="name">Имя</label>
                                <input type="text" id='name'/>
                            </div>
                        </div>
                    </div>
                    <div className="right">
                        <Tilt options={{
                            max : 20,
                            scale: 1,
                            speed:          1000,    // Speed of the enter/exit transition
                            transition:     false,   // Set a transition on enter/exit.
                            axis:           null,
                            reset:          false,
                            reverse:        true,
                            perspective:    900,
                        }} style={{ height: '100%', width: '100%' }} >
                            <SVGlogo />
                        </Tilt>
                    </div>
                </div>
            </div>
        )
    }
}
